package basics;

public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a=1.23f;
		float b=3.786f;
		
		double x=7.87;
		double y=8.98;
		
		float sum1=a+b;
		double sum2=x+y;
		
		System.out.println("Sum of floats "+a+" and "+b+" are "+sum1);
		System.out.println("Sum of doubles "+x+" and "+y+" are "+sum2);
	}

}
