package basics;

public class ElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks=64;
		if(marks>=60){
			System.out.println("First class");
		}
		else if(marks>=50) {
			System.out.println("Second Class");	
		}
		else if(marks>=35) {
			System.out.println("Passed");
		}
		else {
			System.out.println("Fail");
		}

	}

}
