package basics;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=0;
		while(num<3) {
			System.out.println(num);
			num++;
		}
		System.out.println("---");
		int a=0;
		do {
			System.out.println(a);
			a++;
		}while(a<3);
		System.out.println("---");
		for(int i=0; i<3; i++) {
			System.out.println(i);
			
		}

	}

}
